mapper.select:
SELECT
-- 级联表的字段 ,jiaoshi.jiaoshi_name as jiaoshiName ,jiaoshi.sex_types as sexTypes ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber ,jiaoshi.jiaoshi_phone as jiaoshiPhone ,jiaoshi.jiaoshi_photo as jiaoshiPhoto ,shejitimu.shejitimu_name as shejitimuName ,shejitimu.jiaoshi_id as jiaoshiId ,shejitimu.shejitimu_types as shejitimuTypes ,shejitimu.insert_time as insertTime ,shejitimu.shejitimu_zancheng as shejitimuZancheng ,shejitimu.shejitimu_fandui as shejitimuFandui ,shejitimu.zhuangtai_types as zhuangtaiTypes ,shejitimu.shejitimu_content as shejitimuContent ,yonghu.yonghu_name as yonghuName ,yonghu.sex_types as sexTypes ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_photo as yonghuPhoto
FROM xuantishenhe a left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id left JOIN shejitimu shejitimu ON a.shejitimu_id = shejitimu.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.yonghu_id = #{params.yonghuId}
and a.jiaoshi_id = #{params.jiaoshiId}
and a.shejitimu_id = #{params.shejitimuId}
and a.xuantishenhe_types = #{params.xuantishenheTypes}
and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')
and jiaoshi.sex_types = #{params.sexTypes}
and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')
and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')
and shejitimu.shejitimu_name like CONCAT('%',#{params.shejitimuName},'%')
and shejitimu.jiaoshi_id = #{params.jiaoshiId}
and shejitimu.shejitimu_types = #{params.shejitimuTypes}
and UNIX_TIMESTAMP(shejitimu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(shejitimu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and shejitimu.shejitimu_zancheng >= #{params.shejitimuZanchengStart}
and shejitimu.shejitimu_zancheng <= #{params.shejitimuZanchengEnd}
and shejitimu.shejitimu_fandui >= #{params.shejitimuFanduiStart}
and shejitimu.shejitimu_fandui <= #{params.shejitimuFanduiEnd}
and shejitimu.zhuangtai_types = #{params.zhuangtaiTypes}
and shejitimu.shejitimu_content like CONCAT('%',#{params.shejitimuContent},'%')
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.sex_types = #{params.sexTypes}
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
order by a.${params.orderBy} desc
